def get_cached_symbols():
    try:
        conn = get_connection()
        cursor = conn.cursor()
        cursor.execute("SELECT asset_id FROM asset_universe WHERE is_active = 1 ORDER BY asset_id")
        raw_symbols = [row[0] for row in cursor.fetchall()]
        conn.close()
        return sorted(list(set(raw_symbols)))
    except:
        return []

def get_asset_evaluation_history(asset_id: str):
    """è·å–æŒ‡å®šèµ„äº§çš„æ‰€æœ‰å†å²è¯„ä¼°è®°å½•"""
    try:
        conn = get_connection()
        query = """
        SELECT 
            s.snapshot_id,
            s.asset_id,
            s.as_of_date,
            s.created_at,
            s.valuation_status,
            s.risk_level,
            a.name as symbol_name
        FROM analysis_snapshot s
        JOIN assets a ON s.asset_id = a.asset_id
        WHERE s.asset_id = ?
        ORDER BY s.created_at DESC
        """
        df = pd.read_sql_query(query, conn, params=(asset_id,))
        conn.close()
        return df
    except Exception as e:
        st.error(f"è·å–å†å²è®°å½•å¤±è´¥: {str(e)}")
        return pd.DataFrame()

