    c1, c2, c3 = st.columns(3)
    
    with c1:
        # Layout: Max Drawdown -> [Current DD] -> Volatility
        # We concatenate HTML strings to render in one st.markdown block to avoid Streamlit's element spacing gap.
        
        # Helper to get metric HTML string matches render_inline_metric style
        def _get_metric_html(label, value, delta=None, delta_color="normal", help_text="", details=""):
             # Tooltip logic: Restore "onmouse" overlay effect
             # Changed cursor from 'help' (which shows ?) to 'default' or 'pointer' as requested
             cursor_style = 'cursor: default;' if help_text else ''
             
